            email: \"user@invalid\",","                               password:              \"foo\",","                               password_confirmation: \"bar\" }","    end","    assert_template 'users/new'","    assert_select 'div#error_explanation'","    assert_select 'div.field_with_errors'","  end","","  test \"valid signup information with account activation\" do","    get signup_path","    assert_difference 'User.count', 1 do","      post users_path, user: { name:  \"Example User\",","                               email: \"user@example.com\",","                               password:              \"password\",","                               password_confirmation: \"password\" }","    end","    assert_equal 1, ActionMailer::Base.deliveries.size","    user = assigns(:user)","    assert_not user.activated?","    # Try to log in before activation.","    log_in_as(user)","    assert_not is_logged_in?","    # Invalid activation token","    get edit_account_activation_path(\"invalid token\")","    assert_not is_logged_in?","    # Valid token, wrong email","    get edit_account_activation_path(user.activation_token, email: 'wrong')","    assert_not is_logged_in?","    # Valid activation token","    get edit_account_activation_path(user.activation_token, email: user.email)","    assert user.reload.activated?","    follow_redirect!","    assert_template 'users/show'","    assert is_logged_in?","  end"]},{"action":"insertText","range":{"start":{"row":48,"column":0},"end":{"row":48,"column":3}},"text":"end"}]}]]},"ace":{"folds":[],"scrolltop":153,"scrollleft":0,"selection":{"start":{"row":32,"column":38},"end":{"row":32,"column":38},"isBackwards":false},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":0},"timestamp":1415319655150,"hash":"a09eb419dc03e213cdd4d3f699e3d7edec5f6db1"}